             isPre   =   arr [ i ]. tagName . toLowerCase ()   ==   pre ; 
             if   ( isPre )   { 
                 // Handles  pre code 
                 childEl   =   arr [ i ]. firstChild ; 
                 parentEl   =   childEl . parentNode ; 
                 text   =   ; 
